mapper.select:
select count(*)
  from qna_board
 where qna_no = #{qnaNo}

mapper.insert:
insert into qna_board(
						qna_title,
						qna_content,
						qna_userid
			) values (
						#{qnaTitle},
						#{qnaContent},
						#{qnaUserid}
			)

mapper.update:
update qna_board
   set qna_title = #{qnaTitle},
       qna_content = #{qnaContent}
 where qna_no = #{qnaNo}
